INSERT INTO `fh_course` VALUES ('65', '25', '昂立国际少儿英语二级', '<p>昂立国际少儿英语二级上：</p>\r\n\r\n<p>课时：60L 学期：5个月左右 适合年龄：3-6岁 备注:含启航5书300，小精灵398</p>\r\n\r\n<p>昂立国际少儿英语二级下：</p>\r\n\r\n<p>课时：60L 学期：5个月左右 适合年龄：3-6岁 备注:含启航6书300，小精灵398</p>', '入门级', '1年', '自由安排', '语言培训-英语-少儿英语', '/resource/upload/images/2017/11/16/1510802439201039.jpg', '71', '2017-11-16 11:20:39', '1');
