INSERT INTO `fh_course` VALUES ('67', '25', '昂立新进阶少儿英语1级', '<p>昂立新进阶少儿英语1级上：</p>\r\n\r\n<p>课时：81L 学期：6个月左右 适合年龄：8-9岁 备注:含新进阶级1上书和云端作业系统</p>\r\n\r\n<p>昂立新进阶少儿英语1级下：</p>\r\n\r\n<p>课时：81L 学期：6个月左右 适合年龄：8-9岁 备注:含新进阶级1下书和云端作业系统</p>', '入门级', '1年', '自由安排', '语言培训-英语-少儿英语', '/resource/upload/images/2017/11/16/1510802583168041.jpg', '87', '2017-11-16 11:23:03', '1');
