INSERT INTO `fh_course` VALUES ('76', '25', '常州五年级学霸班', '<p>五年级学霸班上：</p>\r\n\r\n<p>　　课时：90L 学期：6个月左右 适合年龄：五年级</p>\r\n\r\n<p>　　五年级学霸班下：</p>\r\n\r\n<p>　　课时：66L 学期：6个月左右 适合年龄：五年级</p>\r\n\r\n<p>　　◆课程价格：可至各校区前台详细咨询</p>\r\n\r\n<p>　　◆开课人数：8-20人 小班授课</p>\r\n\r\n<p>　　◆课时安排：20次/3L 共5个月左右</p>\r\n\r\n<p>　　◆学习内容：</p>\r\n\r\n<p>　　【小呼吸作文一级】</p>\r\n\r\n<p>　　掌握并自如运用写作最基本的句式与方法，为写作打下夯实的基础，培养好的阅读习惯，写出完整、规范、流畅、生动的日记和作文。</p>', '入门级', '1年', '自由安排', '学科辅导-小学-语文', '/resource/upload/images/2017/11/16/1510810309509053.jpg', '74', '2017-11-16 13:31:50', '1');
