DROP TABLE IF EXISTS `sys_setting`;
CREATE TABLE `sys_setting` (
  `id` varchar(64) NOT NULL,
  `adminsite_name` varchar(255) DEFAULT NULL COMMENT '后台网站名称',
  `adminsite_logo` varchar(255) DEFAULT NULL COMMENT '后台网站logo',
  `website_name` varchar(255) DEFAULT NULL COMMENT '前台网站名称',
  `website_logo` varchar(255) DEFAULT NULL COMMENT '前台网站logo',
  `copyright_info` varchar(255) DEFAULT NULL COMMENT '底部版权信息',
  `filing_info` varchar(255) DEFAULT NULL COMMENT 'ICP备案信息',
  `website_desc` varchar(255) DEFAULT NULL COMMENT '描述',
  `statistical_code` varchar(255) DEFAULT NULL COMMENT '统计代码  在商城（前台）各个页面加上第三方流量统计代码，用于记录各页面访问情况',
  `seo_title` varchar(255) DEFAULT NULL,
  `seo_keywords` varchar(255) DEFAULT NULL,
  `seo_desc` varchar(255) DEFAULT NULL,
  `register_agreement` longtext COMMENT '会员注册协议',
  `register_verify_mobile` tinyint(4) DEFAULT NULL COMMENT '会员注册是否需要手机验证(1是0否)',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

